mapper.select:
select
    user_id, user_name, password, role.role_id as role_id, role.role_name as role_name, role.permission as permission,
    sex, phone, email, birthday, age, expire
from
    user
inner join
    role
on
    user.role_id = role.role_id


        user_id = #{userId}


        and user_name = #{userName}


        and password = #{password}



            and role.role_id = #{role.roleId}


            and role.role_name = #{role.roleName}


            and role.permission = #{role.permission}



        and sex = #{sex}


        and phone = #{phone}


        and email = #{email}


        and expire = #{expire}

mapper.update:
update user


        userName = #{userName},


        password = #{password},


            role_id = #{roleId}



        sex = #{sex}


        phone = #{phone},


        email = #{email}


        birthday = #{birthday}


        expire = #{expire}


where
    user_id = #{userId}
